INSERT INTO `tbl_district` (`id`, `district_id`, `district_name`, `district_map`, `country_id`, `district_dec`) VALUES
(1, 20001, 'চাঁপাইনবাবগঞ্জ', 'no', 10001, 'চাঁপাইনবাবগঞ্জ জেলা বাংলাদেশের উত্তর-পশ্চিমাঞ্চলের রাজশাহী বিভাগের একটি প্রশাসনিক অঞ্চল। রাজশাহী বিভাগের অন্তর্গত এই জেলাটিকে কখনো নবাবগঞ্জ এবং চাঁপাই নামেও ডাকা হয়। ভারত উপমহাদেশ বিভাগের আগে এটি মালদহ জেলার একটি অংশ ছিল। ১৯৪৭ সালে এটি মালদহ থেকে আলাদা হয়ে পূর্ব পাকিস্থানে অন্তর্ভুক্ত হয় এবং রাজশাহী জেলার একটি মহাকুমা হিসেবে গন্য হয়। ১৯৮৪ সালে একটি একক জেলা হিসেবে আত্মপ্রকাশ করে। অনেকে চাঁপাইনবাবগঞ্জ কে ''আমের দেশ'' বলেও জানে।'),
(2, 20002, 'কক্সবাজার', 'no', 10001, 'কক্সবাজার বাংলাদেশের দক্ষিণ-পূর্বাঞ্চলে অবস্হিত একটি পর্যটন শহর। এটি চট্টগ্রাম বিভাগের কক্সবাজার জেলার অন্তর্গত। কক্সবাজার তার নৈসর্গিক সৌন্দর্য্যের জন্য বিখ্যাত। এখানে রয়েছে বিশ্বের দীর্ঘতম অবিচ্ছিন্ন প্রাকৃতিক বালুময় সমুদ্র সৈকত যা কক্সবাজার শহর থেকে বদরমোকাম পর্যন্ত একটানা ১৫৫ কিলোমিটার (৯৬ মাইল) পর্যন্ত বিস্তৃত। এখানে রয়েছে বাংলাদেশের বৃহত্তম সামুদ্রিক মৎস্য বন্দর এবং সাবমেরিন ক্যাবল ল্যান্ডিং স্টেশান। একসময় কক্সবাজার পানোয়া নামেও পরিচিত ছিল যার আক্ষরিক অর্থ হচ্ছে হলুদ ফুল। এর আরো একটি প্রাচীন নাম হচ্ছে পালঙ্কি।'),
(3, 20003, 'বান্দরবান', 'no', 10001, 'বান্দরবান পার্বত্য চট্টগ্রামের অংশ। এই অঞ্চলটি ১৫৫০ সালের দিকে প্রণীত বাংলার প্রথম মানচিত্রে বিদ্যমান ছিল। তবে এর প্রায় ৬০০ বছর আগে ৯৫৩ সালে আরাকানের রাজা এই অঞ্চল অধিকার করেন। ১২৪০ সালের দিকে ত্রিপুরার রাজা এই এলাকা দখল করেন। ১৪০০ শতকের দিকে চাকমা রাজা এই অঞ্চল দখল করেন। ১৫৭৫ সালে আরাকানের রাজা এই অঞ্চল আক্রমণ করে কিছু এলাকা পুনর্দখল করেন, এবং ১৬৬৬ সাল পর্যন্ত অধিকারে রাখেন। মুঘল সাম্রাজ্য ১৬৬৬ থেকে ১৭৬০ সাল পর্যন্ত এলাকাটি সুবা বাংলার অধীনে শাসন করে। ১৭৬০ সালে ব্রিটিশ ইস্ট ইন্ডিয়া কোম্পানি এই এলাকা নিজেদের আয়ত্তে আনে। ১৮৬০ সালে এটি ব্রিটিশ ভারতের অংশ হিসাবে যুক্ত হয়। ব্রিটিশরা এই এলাকার নাম দেয় চিটাগাং হিল ট্র্যাক্টস বা পার্বত্য চট্টগ্রাম। এটি চট্টগ্রাম জেলার অংশ হিসাবে বাংলা প্রদেশের অন্তর্গত ছিল। মূলত চিটাগাং হিল ট্র্যাক্টস রেগুলাসন ১৯০০ দ্বারা এই অঞ্চল আনুষ্ঠানিকভাবে আরাকান রাজ্যের অংশ থেকে তৎকালীন ব্রিটিশ সাম্রাজ্যের আওতায় আসে এবং চাকমা সার্কেল, মং সার্কেল ও বোমাং সার্কেল প্রতিষ্ঠিত হয় এবং প্রত্যেক সার্কেলে সার্কেল চীফ বা রাজা নিযুক্ত করা হয়। বান্দরবান জেলা ছিল বোমাং সার্কেলের অন্তর্ভুক্ত। বোমাং রাজ পরিবার ১৬ শতক থেকেই এই অঞ্চল শাসন করছিল। তাই এ জেলার আদি নাম বোমাং থং।'),
(4, 20004, 'কিশোরগঞ্জ', 'no', 10001, 'কিশোরগঞ্জের ইতিহাস সুপ্রাচীন। এখানে প্রাচীনকাল থেকেই একটি সুগঠিত গোষ্ঠী আছে এবং এখনোও তা বিরাজ করছে। ষষ্ঠ শতকে বত্রিশ এর বাসিন্দা কৃষ্ণদাস প্রামাণিকের ছেলে নন্দকিশোর ব্রহ্মপুত্র নদের তীরে একটি গঞ্জ প্রতিষ্ঠা করেন; এ গঞ্জ থেকেই কালক্রমে নন্দকিশোরের গঞ্জ বা ''কিশোরগঞ্জ''-এর উৎপত্তি হয়। একাদশ ও দ্বাদশ শতকে পাল, বর্মণ ও সেন শাসকরা এ অঞ্চলে রাজত্ব করে। তাদের পর ছোট ছোট স্বাধীন গোত্র কোচ, হাজং, গারো এবং রাজবংশীরা এখানে বসবাস করে। ১৪৯১ সালে ময়মনসিংহের অধিকাংশ অঞ্চল ফিরোজ শাহ-এর অধীনে থাকলেও কিশোরগঞ্জ সেই মুসলিম শাসনের বাইরে রয়ে যায়। পরবর্তীতে মুঘল সম্রাট আকবরের সময়কালে বেশিরভাগ অঞ্চল মুঘল সাম্রাজ্যের অধীনে থাকলেও জঙ্গলবাড়ি ও এগারসিন্দুর কোচ ও অহম শাসকদের অধীনে রয়ে যায়। ১৫৩৮ সালে এগারসিন্দুরের অহম শাসক মুঘলদের কাছে ও ১৫৮০ সালে জঙ্গলবাড়ির কোচ শাসক ঈসা খাঁর কাছে পরাজিত হয়। ১৫৮০ সালে বার ভূঁইয়াদের প্রধান ঈসা খাঁ এগারসিন্দুরে আকবরের সেনাপতি মান সিংহকে পরাজিত করেন। ঈসা খাঁর মৃত্যুর পর জঙ্গলবাড়ি ও এগারসিন্দুর তার পুত্র মুসা খাঁর অধীনে আসে কিন্তু ১৫৯৯ সালে তিনি মুঘলদের কাছে পরাজিত হন।'),
(5, 20005, 'ঢাকা', 'no', 10001, 'ঢাকা দক্ষিণ এশিয়ার রাষ্ট্র বাংলাদেশের রাজধানী ও বৃহত্তম শহর। প্রশাসনিকভাবে এটি দেশটির ঢাকা বিভাগের প্রধান শহর। ভৌগোলিকভাবে এটি বাংলাদেশের মধ্যভাগে বুড়িগঙ্গা নদীর উত্তর তীরে একটি সমতল এলাকাতে অবস্থিত। ঢাকা একটি অতিমহানগরী (মেগাশহর); ঢাকা মহানগরী এলাকার জনসংখ্যা প্রায় ১ কোটি ৫০ লক্ষ।[৪] জনসংখ্যার বিচারে এটি দক্ষিণ এশিয়ার চতুর্থ বৃহত্তম শহর (দিল্লি, করাচি ও মুম্বইয়ের পরেই) এবং সমগ্র বিশ্বের নবম বৃহত্তম[৫] শহর। জনঘনত্বের বিচারে ঢাকা বিশ্বের সবচেয়ে ঘনবসতিপূর্ণ মহানগরী; ১৩৪ বর্গমাইল আয়তনের এই শহরে প্রতি বর্গমাইল এলাকায় ১ লক্ষ ১৫ হাজার লোকের বাস।[৬]\r\n\r\nঢাকা শহরটি "মসজিদের শহর" নামেও পরিচিত।[৭] এখানে ৭০০-রও বেশি মসজিদ আছে।[৮] এছাড়া ঢাকা "বিশ্বের রিকশা রাজধানী" নামেও পরিচিত। এই শহরে রোজ প্রায় ৪০০,০০০ থেকে ৪৫০,০০০টি রিকশা চলাচল করে।[৯] বর্তমানে ঢাকা দক্ষিণ এশিয়ার অন্যতম প্রধান সংস্কৃতি, শিক্ষা ও বাণিজ্যকেন্দ্র।[১০]\r\n\r\nঢাকা শহরের জলবায়ু ক্রান্তীয় আর্দ্র ও শুষ্ক প্রকৃতির। গড় তাপমাত্রা এপ্রিল মাসে সর্বোচ্চ প্রায় ৩৪ ডিগ্রী সেলসিয়াস এবং জানুয়ারি মাসে সর্বনিম্ন প্রায় ১৩ ডিগ্রী সেলসিয়াস। মে মাস থেকে সেপ্টেম্বর মাস পর্যন্ত ঢাকা শহরে বর্ষাকাল, সেসময় প্রতি মাসে গড়ে ৩০০ মিলিমিটারের বেশি বৃষ্টি হয়।[১১][১২][১৩][১৪][১৫]\r\n\r\nসপ্তদশ শতাব্দীতে পুরানো ঢাকা মুঘল সাম্রাজ্যের সুবহে বাংলা (বাংলা প্রদেশ) এর প্রাদেশিক রাজধানী ছিলো। মুঘল সম্রাট জাহাঙ্গীরের শাসনামলে এই শহর জাহাঙ্গীর নগর নামে পরিচিত ছিলো। বিশ্বব্যাপী মসলিন বাণিজ্যের একটি কেন্দ্র ছিলো ঢাকা এবং বিশ্বের বিভিন্ন স্থান থেকে ব্যবসায়ীগণ এখানে বাণিজ্যের উদ্দেশ্যে আসতেন। ঢাকাতে বিশ্বের সেরা মসলিন কাপড় উৎপাদিত হতো।[তথ্যসূত্র প্রয়োজন] যদিও আধুনিক ঢাকা শহরের বিকাশ ঘটে ঊনবিংশ শতাব্দীতে ব্রিটিশ শাসন আমলে, এই সময় নবাবগণ ঢাকা শাসন করতেন। এই সময় কলকাতার পরেই ঢাকা বাংলা প্রেসিডেন্সির দ্বিতীয় বৃহত্তম নগরী হয়ে ওঠে। ১৯০৫ সালের বঙ্গভঙ্গের পরে ঢাকা নবগঠিত পূর্ববঙ্গ ও আসাম প্রদেশের রাজধানী হয়। ১৯৪৭ সালে ভারত বিভাগের পরে ঢাকা পূর্ব পাকিস্তানের প্রশাসনিক রাজধানীতে পরিণত হয়। ১৯৫০-৬০ সালের মধ্যে এই শহর বিভিন্ন সামাজিক, জাতীয়তাবাদী ও গণতন্ত্রপন্থী আন্দোলনের কেন্দ্রবিন্দু হয়ে ওঠে। ১৯৭১ সালে ঢাকা স্বাধীন বাংলাদেশ রাষ্ট্রের রাজধানী ঘোষিত হয়। ইতিপূর্বে সামরিক আইন বলবৎ, বাংলাদেশের স্বাধীনতা ঘোষণা, সামরিক দমন, যুদ্ধ ও প্রাকৃতিক বিপর্যয়ের তাণ্ডবলীলার মতো একাধিক অস্থির ঘটনার সাক্ষী হয় এই শহর।\r\n\r\nবাংলাদেশের সংবিধানের ৫(ক) অনুচ্ছেদ অনুযায়ী[১৬] ঢাকা বাংলাদেশের রাজধানী। আধুনিক ঢাকা বাংলাদেশের রাজনৈতিক, সাংস্কৃতিক ও অর্থনৈতিক জীবনের প্রধান কেন্দ্র।[১৭] এটা প্রশংসিত জাতীয় দর্শনীয় স্থানগুলো যেমন জাতীয় সংসদ ভবন,ঢাকা কলেজ, ঢাকা বিশ্ববিদ্যালয়, মুক্তিযুদ্ধ জাদুঘর, জাতীয় জাদুঘর, জাতীয় শহীদ মিনার, লালবাগের কেল্লা , আইডিয়াল স্কুল এন্ড কলেজ[১৮] ইত্যাদির মূলস্থান।\r\n\r\nএই শহরের নগরাঞ্চলীয় অবকাঠামোটি বিশ্বে সর্বোন্নত হলেও দূষণ, যানজট এবং জনসংখ্যা বৃদ্ধির কারণে যথেষ্ট পরিষেবার অভাব ইত্যাদি শহুরে সমস্যাগুলি এখানে প্রকট। সাম্প্রতিক দশকগুলিতে ঢাকার পরিবহন, যোগাযোগ ব্যবস্থা ও গণপূর্ত ব্যবস্থায় যে আধুনিকীকরণ হয়েছে, তা বিশেষভাবে লক্ষণীয়। বর্তমানে এই শহর প্রচুর বিদেশী বিনিয়োগ টানতে এবং ব্যবসা-বাণিজ্যের পরিধি বাড়াতে সক্ষম হয়েছে। সারা দেশ থেকে প্রচুর মানুষ ঢাকায় আসেন জীবন ও জীবিকার সন্ধানে। এ কারণে ঢাকা হয়ে উঠেছে বিশ্বের দ্রুততম ক্রমবর্ধমান নগরী, এই লক্ষ বাস্তবায়নে মালয়েশিয়া, জাপান, চীন সহ বিভিন্ন দেশ অর্থ সহযোগীতা ও বিনিয়োগ করছে'),
(6, 20006, 'কুমিল্লা', 'no', 10001, 'শিক্ষা-শিল্প-সাহিত্য সংস্কৃতির পাদপীঠ কুমিল্লা প্রাচীন ঐতিহ্য সমৃদ্ধ জেলা হিসেবে এ উপমহাদেশে সুপরিচিত। কুমিল্লার খাদি শিল্প, তাঁত শিল্প, কুটির শিল্প, মৃৎ শিল্প ও কারু শিল্প, রসমালাই, মিষ্টি, ময়নামতির শীতল পাটি ইত্যাদি স্ব-স্ব ঐতিহ্যে স্বকীয়তা আজও বজায় রেখেছে।[তথ্যসূত্র প্রয়োজন] কালের বিবর্তনের ধারায় এসেছে অনেক কিছু, অনেক কিছু গেছে হারিয়ে, হারায়নি এখানকার মানুষের আন্তরিকতাপূর্ণ আতিথেয়তা ও সামাজিক সম্প্রীতি।\r\n\r\n\r\nকুমিল্লার শালবন বিহার\r\nকুমিল্লা একসময় বর্তমান ভারতের ত্রিপুরা রাজ্যের অংশ ছিল। ১৭৩৩ সালে বাংলার নবাব শুজাউদ্দিন ত্রিপুরা রাজ্য আক্রমণ করে এর সমতল অংশ সুবে বাংলার অন্তর্ভুক্ত করেন। ১৭৬৫ সালে ইস্ট ইন্ডিয়া কোম্পানী ত্রিপুরা দখল করে॥ ১৭৯০ সালে কোম্পানী শাসনামলে ত্রিপুরা নামের জেলার সৃষ্টি। পরবর্তীতে ১৯৬০ সালে বর্তমান ভারতের ত্রিপুরা থেকে আলাদা করে কুমিল্লাকে জেলা হিসেবে মর্যাদা দেয়া হয়।\r\n\r\nবর্তমান কুমিল্লা জেলা চট্টগ্রাম বিভাগের অধীনস্থ একটি জেলা। শুরুর দিকে এটি সমতট জনপদের অন্তর্গত হলেও পরবর্তীকালে এটি ত্রিপুরা রাজ্যের অংশ হয়েছিল। কুমিল্লা নামকরণের অনেকগুলো প্রচলিত মত রয়েছে। যার মধ্যে উল্লেখযাগ্য চৈনিক পরিব্রাজক ওয়াং চোয়াঙ কর্তৃক সমতট রাজ্য পরিভ্রমণের বৃত্তান্ত থেকে। তার বর্ণনায় কিয়া-মল-ঙ্কিয়া নামক যে স্থানের বিবরণ রয়েছে সেটি থেকে কমলাঙ্ক বা কুমিল্লার নামকরণ হয়েছে। এ অঞ্চলে প্রাপ্ত প্রাচীন নিদর্শন‍াদি থেকে জানা যায় খ্রিষ্টীয় পঞ্চম শতাব্দী থেকে ত্রিপুরা গুপ্ত সম্রাটদের অধিকারভুক্ত ছিল।');
